// src/App.jsx
import React, { useState, useEffect } from 'react';
import WelcomePage from './components/WelcomePage';
import PlayerList from './components/PlayerList';
import socket from './utilities/socket';

function App() {
  const [players, setPlayers] = useState([]);
  const [gameState, setGameState] = useState(null);
  const [page, setPage] = useState('welcome'); // 'welcome' or 'game'

  useEffect(() => {
    // Listen for game state updates from the backend
    socket.on('gameStateUpdate', (newGameState) => {
      setGameState(newGameState);
    });

    return () => {
      socket.off('gameStateUpdate'); // Cleanup listener on unmount
    };
  }, []);

  const handlePlayerAction = (action) => {
    socket.emit('playerAction', action);  // Send action to backend
  };

  return (
    <div className="App">
      {page === 'welcome' ? (
        <WelcomePage onStartGame={(numPlayers) => {
          setPlayers(Array.from({ length: numPlayers }, (_, index) => `Player ${index + 1}`));
          setPage('game');
        }} />
      ) : (
        <PlayerList
          players={players}
          gameState={gameState}
          onPlayerAction={handlePlayerAction}
        />
      )}
    </div>
  );
}

export default App;
