import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

function App() {
  const [socket, setSocket] = useState(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [playerName, setPlayerName] = useState('');
  const [betAmount, setBetAmount] = useState(0);

  useEffect(() => {
    const socketIo = io('http://localhost:8080');
    setSocket(socketIo); // Store the socket instance

    // Listen for messages from the server
    socketIo.on('message', (msg) => {
      setMessages((prevMessages) => [...prevMessages, msg]);
    });

    // Cleanup when component is unmounted
    return () => {
      if (socketIo) {
        socketIo.disconnect();
      }
    };
  }, []);

  // Send message to the WebSocket server
  const sendMessage = (action, data) => {
    if (socket) {
      const messageObj = { action, ...data };
      socket.emit(action, messageObj);
    }
  };

  // Handle user join action
  const handleJoin = () => {
    sendMessage('join', { name: playerName });
  };

  // Handle deal action
  const handleDeal = () => {
    sendMessage('deal', {});
  };

  // Handle bet action
  const handleBet = () => {
    sendMessage('bet', { name: playerName, amount: betAmount });
  };

  return (
    <div>
      <h1>Texas Hold'em Game</h1>

      <div>
        <input
          type="text"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          placeholder="Enter your name"
        />
        <button onClick={handleJoin}>Join Game</button>
      </div>

      <div>
        <button onClick={handleDeal}>Deal Cards</button>
      </div>

      <div>
        <input
          type="number"
          value={betAmount}
          onChange={(e) => setBetAmount(e.target.value)}
          placeholder="Enter Bet Amount"
        />
        <button onClick={handleBet}>Place Bet</button>
      </div>

      <div>
        <h3>Game Messages</h3>
        <ul>
          {messages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
