import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import io from "socket.io-client";
import Player from "../components/Player"; // Component to display player info
//import Card from "./Card"; // Assuming you have a Card component

const socket = io("http://localhost:8080");

function Game() {
  const { tableId, playerName } = useParams(); // Get tableId and playerName from URL params
  const [gameState, setGameState] = useState(null);
  const [action, setAction] = useState(""); // Track the current player's action

  useEffect(() => {
    // Join the game when the component mounts
    socket.emit("joinGame", { tableId, playerName });

    // Listen for game state updates
    socket.on("updateGameState", (updatedGameState) => {
      setGameState(updatedGameState);
    });

    // Clean up when the component unmounts
    return () => {
      socket.off("updateGameState");
    };
  }, [tableId, playerName]);

  const handleAction = (player) => {
    if (!gameState) return;

    // Send the player's action to the server
    socket.emit("playerAction", { tableId, action });

    // Reset action after sending it
    setAction("");
  };

  const renderPlayers = () => {
    if (!gameState) return null;

    return gameState.table.players.map((player) => (
      <Player key={player.socketId} player={player} handleAction={handleAction} />
    ));
  };

  const handleBet = () => {
    setAction("bet");
  };

  const handleFold = () => {
    setAction("fold");
  };

  return (
    <div className="game">
      <h1>Poker Game - Table: {tableId}</h1>
      <p>Welcome, {playerName}!</p>

      {/* Render the players */}
      <div className="players">{renderPlayers()}</div>

      {/* Actions (Bet, Fold, etc.) */}
      <div className="actions">
        <button onClick={handleBet}>Bet</button>
        <button onClick={handleFold}>Fold</button>
      </div>

      {/* Display the current action */}
      <div className="current-action">
        <p>Current Action: {action}</p>
      </div>

      {/* Display the table and pot */}
      <div className="table-info">
        <p>Pot: {gameState ? gameState.table.pot : 0}</p>
      </div>
    </div>
  );
}

export default Game;
