import React, { useState, useEffect } from 'react';

function App() {
  const [socket, setSocket] = useState(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);

  // Establish WebSocket connection when component mounts
  useEffect(() => {
    const ws = new WebSocket('ws://localhost:8080'); // Replace with your server address if needed
    ws.onopen = () => {
      console.log('Connected to WebSocket server');
    };
    ws.onmessage = (event) => {
      const receivedData = JSON.parse(event.data);
      console.log('Received:', receivedData);
      setMessages((prevMessages) => [...prevMessages, receivedData.message]);
    };
    ws.onclose = () => {
      console.log('Disconnected from WebSocket server');
    };
    ws.onerror = (error) => {
      console.error('WebSocket Error:', error);
    };

    setSocket(ws); // Store the WebSocket instance

    // Clean up the WebSocket connection on component unmount
    return () => {
      if (ws) {
        ws.close();
      }
    };
  }, []);

  // Send message to the WebSocket server
  const sendMessage = () => {
    if (socket && message.trim() !== '') {
      const messageObj = { action: 'sendMessage', message: message };
      socket.send(JSON.stringify(messageObj)); // Send message as JSON
      setMessage(''); // Clear input field after sending
    }
  };

  return (
    <div>
      <h1>WebSocket Chat</h1>
      <div>
        <h3>Messages</h3>
        <ul>
          {messages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <div>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type a message"
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default App;
